import './App.css';
import { useState, useEffect, useCallback } from 'react';
import { ethers } from 'ethers';
import { db } from './FirebaseConfig';
import { collection, addDoc, onSnapshot } from "firebase/firestore";
import VotingArtifact from './contracts/Voting.json';

const contractAddress = "0x5FbDB2315678afecb367f032d93F642f64180aa3";

function App() {
  const [account, setAccount] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [contract, setContract] = useState(null);
  const [candidates, setCandidates] = useState([]);
  const [totalVotes, setTotalVotes] = useState(0);

  // Load candidates from contract
  const loadCandidates = useCallback(async (contractInstance) => {
    try {
      setLoading(true);
      const count = await contractInstance.candidateCount();
      const loadedCandidates = [];
      let votes = 0;

      for (let i = 1; i <= count; i++) {
        const candidate = await contractInstance.candidates(i);
        loadedCandidates.push({
          id: i,
          name: candidate.name,
          voteCount: candidate.voteCount.toString()
        });
        votes += parseInt(candidate.voteCount.toString());
      }

      setCandidates(loadedCandidates);
      setTotalVotes(votes);
      setLoading(false);
    } catch (err) {
      console.error("Error loading candidates:", err);
      setError("Failed to load candidates");
      setLoading(false);
    }
  }, []);

  // Save vote to Firebase
  const saveVoteToDB = useCallback(async (candidateId) => {
    try {
      await addDoc(collection(db, "votes"), {
        candidateId,
        voter: account,
        timestamp: new Date()
      });
    } catch (e) {
      console.error("Error saving vote: ", e);
      setError("Failed to save vote record");
    }
  }, [account]);

  // Handle voting
  const vote = useCallback(async (candidateId) => {
    try {
      setLoading(true);
      const tx = await contract.vote(candidateId);
      await tx.wait(); // Wait for transaction to be mined
      await saveVoteToDB(candidateId);
      await loadCandidates(contract);
    } catch (err) {
      console.error("Error voting:", err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, [contract, saveVoteToDB, loadCandidates]);

  // Initialize contract and load data
  useEffect(() => {
    const initContract = async () => {
      try {
        if (!window.ethereum) throw new Error("Please install MetaMask!");

        const provider = new ethers.BrowserProvider(window.ethereum);
        const signer = await provider.getSigner();
        const contract = new ethers.Contract(
          contractAddress,
          VotingArtifact.abi,
          signer
        );

        setContract(contract);
        await loadCandidates(contract);

        // Get initial account
        const accounts = await window.ethereum.request({ method: 'eth_accounts' });
        if (accounts.length > 0) {
          setAccount(accounts[0]);
        }

        // Handle account changes
        window.ethereum.on('accountsChanged', (accounts) => {
          setAccount(accounts[0] || '');
          window.location.reload();
        });

      } catch (err) {
        console.error("Initialization error:", err);
        setError(err.message);
        setLoading(false);
      }
    };

    initContract();
  }, [loadCandidates]);

  // Connect wallet handler
  const connectWallet = async () => {
    try {
      const accounts = await window.ethereum.request({ 
        method: 'eth_requestAccounts' 
      });
      setAccount(accounts[0]);
    } catch (err) {
      console.error("Error connecting wallet:", err);
      setError(err.message);
    }
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>Decentralized Voting System</h1>

        {!account ? (
          <button className="connect-wallet-btn" onClick={connectWallet}>
            Connect Wallet
          </button>
        ) : (
          <div className="wallet-info">
            <p>Connected as: <span className="account-address">{account}</span></p>
          </div>
        )}

        {error && <div className="error-message">{error}</div>}

        <div className="voting-section">
          <h2>Candidates</h2>
          <p className="total-votes">Total Votes Cast: {totalVotes}</p>

          {loading ? (
            <div className="loading">Loading candidates...</div>
          ) : candidates.length > 0 ? (
            <div className="candidates-grid">
              {candidates.map((candidate) => (
                <div key={candidate.id} className="candidate-card">
                  <h3>{candidate.name}</h3>
                  <p>Votes: {candidate.voteCount}</p>
                  <button 
                    className="vote-btn"
                    onClick={() => vote(candidate.id)} 
                    disabled={!account || loading}
                  >
                    {loading ? 'Processing...' : 'Vote'}
                  </button>
                </div>
              ))}
            </div>
          ) : (
            <p className="no-candidates">No candidates found</p>
          )}
        </div>
      </header>
    </div>
  );
}

export default App;
